import styled from "styled-components"

export const LoginPageWrapper=styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const ImgLogo=styled.img`
  height: 70px;
  margin: 0 auto 20px;
  display: block;
`;
